use reqwest;
use rand::Rng;

pub fn get_word(lang: String) -> String {
    if lang == "1" {
        return get_english_word();
    } else if lang == "2" {
        return get_portuguese_word().to_string();
    } else {
        return String::from("error");
    }
}

fn get_english_word() -> String {
    let body = reqwest::blocking::get("https://random-word-api.herokuapp.com/word?number=1").unwrap().text().unwrap();

    let filtered_body = body.replace("[", "").replace("]", "").replace("\"", "");

    return String::from(filtered_body);
}

